package br.com.elvisassis;import java.text.DateFormat;import java.text.SimpleDateFormat;import java.time.OffsetDateTime;import java.util.Calendar;public class Dates {    public static void main(String[] args) {        var calendar = Calendar.getInstance();        DateFormat formater = new SimpleDateFormat("dd/HH/yyyy HH:mm:ss Z");        System.out.println(formater.format(calendar.get(Calendar.YEAR)));    }}